body {
    padding: 0;
    font-family: 'Roboto', 'Arial', 'sans-serif';
    background-color: $bg-color;
    color: $secondary-text;
}

a {
    text-decoration: none;
    display: block;
    box-sizing: border-box;
    color: #000;

    &:hover {
        text-decoration: none;
    }
}

ul {
    list-style: none;
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

p,
li,
nav,
h1,
h2,
h3,
div,
fieldset,
input {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}


button {
    border: none;
    margin: 0;
    padding: 0;
    font-size: 1rem;
    line-height: 1;
    background: transparent;
    cursor: pointer;
    outline: none;
  }

input {
    outline: none;
}

