.menu {

  width: 910px;
  height: 90px;
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);

  display: flex;
  justify-content: space-around;
  align-items: center;
  background-color: $bg-color;

  border-radius: 45px;

  animation: bubbling .8s;

  &__link {
    height: 100%;
    min-width: 90px;
    text-align: center;
    padding-top: 57px;
    background-repeat: no-repeat;
    background-position: center 15px;
    color: #fff;
    font-size: 16px;
    font-weight: normal;

    &--feed {
      background-image: url('./assets/img/icon_home.svg');
    }

    &--gallery {
      background-image: url('./assets/img/icon_gallery.svg');
    }

    &--deals {
      background-image: url('./assets/img/icon_deal.svg');
    }

    &--notify {
      background-image: url('./assets/img/icon_notify.svg');
    }

    &--profile {
      background-image: url('./assets/img/icon_profile.svg');
    }
  }




}

@keyframes bubbling {
  from {
    bottom: -110px;
    left: 50%;
    transform: translateX(-50%);
  }
  70% {
    bottom: 30px;
    left: 50%;
    transform: translateX(-50%);
  }
  to {
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
  }
}

@media screen and (max-width: 860px) {
  .menu {
    width: 100%;
    height: 60px;
    bottom: 0;
    border-radius: 0;
    
    &__link {
      font-size: 0;
      min-width: 40px;
    }
  }
}

